pragma solidity ^0.4.18;

import { Base1 } from "./Base1.sol";
import { Base2 } from "./Base2.sol";
import { ERC20 } from "./ERC20.sol";

/* Eventually Replace With 1 ERC20 Wrapper
contract ERC20 {
  function transfer(address _to, uint256 _value) public returns (bool success);
}
*/

contract CoinWar {

  Base1 public base1;
  Base2 public base2;

  address public owner;
  address public winner;

  uint public fromBlock;
  uint public toBlock;


  mapping (address => uint) public tokenOneMapping;
  mapping (address => uint) public tokenTwoMapping;

  uint public totalTokenOneBet;
  uint public totalTokenTwoBet;
  uint public burnAmount;
  uint public ownerFee;



  event LogAddress(address addr);
  event LogString(string str);
  event LogUint(uint number);

  function CoinWar(address _base1, address _base2, uint _fromBlock, uint _toBlock)
    public
  {
    base1 = Base1(_base1);
    base2 = Base2(_base2);

    fromBlock = _fromBlock;
    toBlock = _toBlock;


    owner = msg.sender;
    //base1.transferFrom();

  }

  function setResults(address[] tokenOneAddr, uint[] tokenOneBets, address[] tokenTwoAddr, uint[] tokenTwoBets, address winningContract)
    public
  {
    require(tokenOneAddr.length == tokenOneBets.length);
    require(tokenTwoAddr.length == tokenTwoBets.length);

    winner = winningContract;

    for (uint i = 0; i < tokenOneAddr.length; i++) {
      tokenOneMapping[tokenOneAddr[i]] = tokenOneBets[i];
      totalTokenOneBet += tokenOneBets[i];
    }

    for (uint j = 0; j < tokenTwoAddr.length; j++) {
      tokenTwoMapping[tokenTwoAddr[j]] = tokenTwoBets[j];
      totalTokenTwoBet += tokenTwoBets[j];
    }


    //burn and withdraw

  }

  function awardWinnerAndBurnLooser()
    public
  {




    if (winner == address(base1) && totalTokenTwoBet != 0) {

        burnAmount = fractionOf(totalTokenTwoBet, 1, 10);
        ownerFee = fractionOf(totalTokenTwoBet, 1, 20);

        totalTokenTwoBet -= burnAmount;
        totalTokenTwoBet -= ownerFee;

        //"0x000000000000000000000000000000000000dEaD" --> burn address to be added
        base2.transfer(address(base2), burnAmount);

    } else if (winner == address(base2) && totalTokenOneBet != 0) {

        burnAmount = fractionOf(totalTokenOneBet, 1, 10);
        ownerFee = fractionOf(totalTokenOneBet, 1, 20);

        totalTokenOneBet -= burnAmount;
        totalTokenOneBet -= ownerFee;


        //"0x000000000000000000000000000000000000dEaD" --> burn address to be added
        base1.transfer(address(base1), burnAmount);

    } else {

    }

  }

  function withdraw(address userAddress)
    public
  {
    //check if the game is over
    uint amount;
    if (winner == address(base1)) {

      require(tokenOneMapping[userAddress] != 0);
      require(totalTokenOneBet != 0);

      amount = fractionOf(totalTokenTwoBet, tokenOneMapping[userAddress], totalTokenOneBet);
      base2.transfer(userAddress, amount);
      base1.transfer(userAddress, tokenOneMapping[userAddress]);

    } else if (winner == address(base2)) {
      require(tokenTwoMapping[userAddress] != 0);
      require(totalTokenTwoBet != 0);
      amount = fractionOf(totalTokenOneBet, tokenTwoMapping[userAddress], totalTokenTwoBet);
      base1.transfer(userAddress, amount);
      base2.transfer(userAddress, tokenTwoMapping[userAddress]);


    } else {

    }

  }

  function fractionOf(uint amount, uint numerator, uint denominator)
    public
    pure
    returns (uint)
  {

    if (amount >= denominator)
      return (amount*numerator)/denominator;

    uint scalar;
    scalar = (denominator/amount) + 1;

    return (amount*numerator*scalar)/(denominator*scalar);

  }


  function GetUserBet(address user)
    public
    view
    returns (uint)
  {
    if(tokenOneMapping[user] != 0)
      return tokenOneMapping[user];
    return tokenTwoMapping[user];
    //return totalTokenOneBet;
  }











}
