pragma solidity ^0.4.18;


import { CoinWar } from "./CoinWar.sol";

contract WarFactory {


  struct War {
    string opponents;
    CoinWar coinWar;
    bool isOngoing;

  }

  War[] public allWars;
  uint public ongGoingWars;

  address public owner;




  function WarFactory()
    public
  {
    //dummy values
    /*allWars.push(War("MMT GGX", true));
    allWars.push(War("EOS TRX", true));
    allWars.push(War("VEN OMG", true));
    allWars.push(War("ICX BNB", true));
    allWars.push(War("DGD PPT", true));
    allWars.push(War("MKR SNT", true));*/
    //ongGoingWars = 5;
    owner = msg.sender;

  }

  function CreateCoinWar(string _opponents, address _tokeOne, address _tokenTwo, uint _fromBlock, uint _toBlock)
    public
  {
    ongGoingWars += 1;
    CoinWar newWar = new CoinWar(_tokeOne, _tokenTwo, _fromBlock, _toBlock);
    allWars.push(War(_opponents, newWar, true));
  }

  function GetWarAtIndex(uint index)
    public
    view
    returns (string,address)
  {
    return (allWars[index].opponents, allWars[index].coinWar);
  }

  function CloseWarAtIndex(uint index)
    public
  {
    ongGoingWars -= 1;
    allWars[index].isOngoing = false;
  }

  //function


}
